<script>
import cookies from '@/data/cookies';

export default {
    data() {
        return {
            show: false,
            showText: false,
            showGlow: false,
            showCookie: false,
            animation: false,
            cookies,
            text: ''
        };
    },
    methods: {
        open() {
            this.animation = true

            setTimeout(() => {
                this.showGlow = true
            }, 1600);

            setTimeout(() => {
                this.showCookie = true
            }, 1900);

            setTimeout(() => {
                this.showCookie = false
            }, 2900);

            setTimeout(() => {
                this.animation = false
                this.show = true
                this.showText = true
                let number = Math.round(Math.random() * (30 - 1) + 1)
                this.cookies = cookies.find(cookie => cookie.id === number).text
            }, 3200);
        },
        openAgane() {
            this.showGlow = false
            this.showText = false
            this.showGlow = false

            setTimeout(() => {
                this.animation = true
            }, 300);

            setTimeout(() => {
                this.showGlow = true
            }, 1600);

            setTimeout(() => {
                this.showCookie = true
            }, 1900);

            setTimeout(() => {
                this.showCookie = false
            }, 2900);

            setTimeout(() => {
                this.animation = false
                this.showText = true
                let number = Math.round(Math.random() * (30 - 1) + 1)
                this.cookies = cookies.find(cookie => cookie.id === number).text
            }, 3200);
        }
    }
}
